SELECT count(*)
FROM `todays_waybill`
WHERE 1 = 1

    AND `open_id` = #{openId}


    AND `pay_status` = #{payStatus}


    AND `state` = #{state}

SELECT
FROM ( SELECT * FROM `todays_waybill` WHERE 1 = 1
AND `open_id` = #{openId}
AND `pay_status` = #{payStatus}
AND `state` = #{state}
) AS `tw`
ORDER BY `tw`.`pay_status` ASC, `tw`.`create_time` DESC, `twl`.`create_time` ASC, (CASE WHEN `twl`.`state` = 0 THEN 0 ELSE 1 END)

SELECT count(*) FROM `todays_waybill` WHERE 1 = 1
AND `td_no` LIKE "%"#{wbNo}"%"
AND `state` = #{state}
AND `open_id` IN (SELECT v_user_id FROM vip_user WHERE type = 1)
AND `open_id` = #{vipUserId}

SELECT

FROM (
SELECT *
