WHERE 1 = 1

    AND `td_no` LIKE "%"#{wbNo}"%"


    AND `state` = #{state}


    AND `open_id` IN (SELECT v_user_id FROM vip_user WHERE type = 1)



        AND `open_id` = #{vipUserId}



) AS `tw`

SELECT `id`
FROM (SELECT *
FROM `todays_waybill`
WHERE `state` = #{state}
AND `id` IN (SELECT `wb_id`
FROM `todays_wb_log`
WHERE `courier_id` = #{courierId}


        AND `state` <= #{state} AND `state` > 0


